let currentDeck = [];
let currentIndex = 0;

// Function to shuffle the deck
function shuffleDeck(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}

// Function to open a specific deck
function openDeck(deckName) {
    currentDeck = [...decks[deckName]]; // Clone the deck
    shuffleDeck(currentDeck);
    currentIndex = 0;
    displayCard();
    document.getElementById("menu").classList.add("hidden");
    document.getElementById("cardViewer").classList.remove("hidden");
}

// Function to display the current card
function displayCard() {
    document.getElementById("card").innerText = currentDeck[currentIndex];
    document.getElementById("cardPosition").innerText = (currentIndex + 1) + "/" + currentDeck.length;
}

// Function to show the next card
function showNextCard() {
    if (currentIndex < currentDeck.length - 1) {
        currentIndex++;
        displayCard();
    }
}

// Function to show the previous card
function showPreviousCard() {
    if (currentIndex > 0) {
        currentIndex--;
        displayCard();
    }
}

// Function to reshuffle the current deck
function reshuffleDeck() {
    if (confirm("Are you sure you want to reshuffle the cards?")) {
        shuffleDeck(currentDeck);
        currentIndex = 0;
        displayCard();
    }
}

// Function to toggle the language - Placeholder for now
function toggleLanguage() {
    alert("Language toggled!");
}

// Function to return to the menu
function returnToMenu() {
    document.getElementById("menu").classList.remove("hidden");
    document.getElementById("cardViewer").classList.add("hidden");
}

// Function to generate the menu dynamically
function generateMenu() {
    const menu = document.getElementById("deckMenu");
    menu.innerHTML = ""; // Clear existing menu items

    for (const deckName in decks) {
        const deckItem = document.createElement("li");
        deckItem.innerText = deckName; // Extracting the number from deckName
        deckItem.onclick = function() { openDeck(deckName); };
        menu.appendChild(deckItem);
    }
}

// Call generateMenu when the script loads
generateMenu();

